mapper.update:
update
    shopping_car
set
    number = #{number}
where
    id = #{id}

mapper.select:
select

from
    g_book gb
    left join category ca on ca.id = gb.category_id


        ca.category_name = #{type}


        gb.title = #{name}


        gb.author = #{author}


        gb.seller_id = #{sellerId}
